    if (doc.accountTo_id.split('.')[1] == 'cash' || doc.accountTo_id.split('.')[1] == 'bank' || doc.accountTo_id.split('.')[1] == 'check') {
      if (doc.state == 'WAITING'){
        emit([doc.accountTo_id, doc.closed, doc.close_id, doc.both, doc._id], 0);  
      } else {
        emit([doc.accountTo_id, doc.closed, doc.close_id, doc.both, doc._id], doc.amount);    
      }
    }
    if (doc.accountFrom_id.split('.')[1] == 'cash' || doc.accountFrom_id.split('.')[1] == 'bank' || doc.accountFrom_id.split('.')[1] == 'check') {
      if (doc.state == 'WAITING'){
        emit([doc.accountFrom_id, doc.closed, doc.close_id, doc.both, doc._id], 0);
      } else {
        emit([doc.accountFrom_id, doc.closed, doc.close_id, doc.both, doc._id], -(doc.amount));
      }
    }
  }
}

