    emit(doc._id, true);
  } else
  if (doc._id.split("/")[0]=='_design'){
    emit(doc._id, true);
  } else
  if (doc._id.split(".")[0]=='sale' && doc.contact_id == 'contact.loxamir@gmail.com'){
      emit(doc._id, true);
  } else
  if (doc._id.split(".")[0]=='stock-move' && doc.contact_id == 'contact.loxamir@gmail.com'){
